#define N 5000
#define M 500'000
struct nail {
    int a, b, x;
} nails[M];
map<int, int> intervals[N+1];
void insert_interval(int row, int s, int e) {
    if(intervals[row].empty()) {
        intervals[row][s] = e;
    } else {
        auto p = le(intervals[row], s);
        int cur;
        if(p != intervals[row].end() and p->second + 1 >= s) {
            intervals[row][p->first] = max(intervals[row][p->first], e);
            cur = p->first;
        } else {
            intervals[row][s] = e;
            cur = s;
        }
        vector<int> dead;
        for(auto it = gt(intervals[row], cur); it != intervals[row].end(); it++) {
            if(it->first - 1 <= intervals[row][cur]) {
                intervals[row][cur] = max(intervals[row][cur], it->second);
                dead.push_back(it->first);
            }
        }
        for(int x : dead) {
            intervals[row].erase(x);
        }
    }
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    int n, m;
    cin >> n >> m;
    for(int i = 0; i < m; i++) {
        cin >> nails[i].a >> nails[i].b >> nails[i].x;
    }
    sort(nails, nails + m, [](nail& n1, nail& n2) {
        return n1.b == n2.b ? n1.a < n2.a : n1.b < n2.b;
    });
    int max_row = 0;
    for(int i = 0; i < m; i++) {
        if(i == 0 or nails[i].b > nails[i-1].b) {
            max_row = nails[i].a;
        }
        for(int row = max_row; row <= nails[i].a + nails[i].x; row++, max_row++) {
            insert_interval(row, nails[i].b, row - (nails[i].a - nails[i].b));
        }
    }
    int covered = 0;
    for(int i = 1; i <= n; i++) {
        for(auto p : intervals[i]) {
            covered += p.second - p.first + 1;
        }
