            tri[x][y]+=tri[x-1][y-1];
            y++,x++;
        }
    }
    for(int i=1;i<=n;i++){
        for(int j=1;j<=i;j++){
            if(tri[j][i]>=1)ans++;
           // printf("%d%c",tri[j][i],j==n?'\n':' ');
        }
    }
    printf("%d\n",ans);
    return 0;
}

