int main()
{
	cin.tie( 0 );
	ios::sync_with_stdio( false );

	int n, m;
	cin >> n >> m;

	VVI board( n + 2 );

	REP( i, 0, board.size() )
	{
		board[i].resize( i + 2, 0 );
	}

	REP( i, 0, m )
	{
		int a, b, x;
		cin >> a >> b >> x;

		a--;
		b--;

		board[a][b] ++;
		board[a][ b + 1 ] --;

		board[ a + x + 1 ][b] --;
		board[ a + x + 1 ][ b + x + 2 ] ++;

		board[ a + x + 2 ][ b + 1 ] ++;
		board[ a + x + 2 ][ b + x + 2 ] --;
	}

	REP( i, 0, board.size() )
	{
		REP( j, 1, board[i].size() )
		{
			board[i][j] += board[i][ j - 1 ];
		}
	}

	REP( i, 1, board.size() )
	{
		REP( j, 0, board[ i - 1 ].size() )
		{
			board[i][j] += board[ i - 1 ][j];
		}
	}	

	REP( i, 1, board.size() )
	{
		REP( j, 1, board[i].size() )
		{
			board[i][j] += board[ i - 1 ][ j - 1 ];
		}
	}

	int res = 0;

	REP( i, 0, board.size() - 2 )
	{
		REP( j, 0, board[i].size() - 1 )
		{
			res += 1 <= board[i][j];
		}
	}

	cout << res << endl;

	return 0;
}

