using namespace std;

int N, M;
vector< vector<bool> > ts;
vector< vector<int> > done;
void init() {
    cin >> N >> M;
    ts.resize(N);
    done.resize(N);
    for (int i = 0; i < N; i++) {
        ts[i].resize(i+1, false);
        done[i].resize(i+1, -1);
    }
}

void enclose(int a, int b, int x) {
    if (done[a][b] < x) {
        ts[a][b] = true; if (x == 0) return;
        enclose(a+1, b, x-1);
        enclose(a+1, b+1, x-1);
        done[a][b] = x;
    }
}

int main() {
    init();
    for (int i = 0; i < M; i++) {
        int A, B, X;
        cin >> A >> B >> X;
        A--, B--;
        enclose(A, B, X);
    }

    int cnt = 0;
    for (int i = 0; i < N; i++) 
        for (int j = 0; j < ts[i].size(); j++) 
            if (ts[i][j]) cnt++;
    cout << cnt << endl;

    return 0;
}

