	cin>>N>>M;
	for(int i=0;i<M;i++){
		cin>>a>>b>>x;
		for(int j=0;j<=x;j++){
			pq[a+j].push(MP(b,j));
		}
	}
	res=0;
	for(int i=1;i<=N;i++){
		cnt=nkr=ima=0;
		while(!pq[i].empty()){
			pa=pq[i].top();pq[i].pop();
			//cout<<pa.first<<" "<<pa.second;
			nkr-=(pa.first-ima);
			ima=pa.first;
			if(nkr<0){
				cnt-=nkr;
				cnt--;
			}
			nkr=max(nkr,pa.second);
		}
		nkr-=(i+1-ima);
		if(nkr<0){
			cnt-=nkr;
			cnt--;
		}
		res+=cnt;
		//cout<<cnt<<endl;//
	}
	cout<<N*(N+1)/2-res<<endl;
	return 0;
