	for (int i = -1; i < n; i++) {
		for (int j = 1; i + j <= n; j++) {
			if (i + j <= 0) continue;
			imos3[i + j][j] += imos3[i + j - 1][j - 1];
		}
	}
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			imos[i][j] += imos2[i][j];
			imos[i][j] -= imos3[i][j];
		}
	}
