#include <stdio.h>

#define calc(x,y) ((((x)*((x)+1))/2)+(y))
#define safe_calc(x, y) ((((x)+2)*((x)+3)/2)+(y)+1)

int n;
int sum;
int nails[13000000];

void process() {
    int i;

    for (i=1;i<n+3;i++) {
        int j;
        for (j=1;j<=i;j++) {
            nails[calc(i,j)] += nails[calc(i,j)-1];
        }
    }

    for (i=0;i<n+3;i++) {
        int j;
        for (j=1;j<(n+3-i);j++) {
            nails[calc(i+j,i)] += nails[calc(i+j-1,i)];
        }
    }

    for (i=0;i<n+3;i++) {
        int j;
        for (j=1;j<(n+3-i);j++) {
            nails[calc(i+j,j)] += nails[calc(i+j-1,j-1)];
        }
    }

    for (i=0;i<n+3;i++) {
        int j;
        for (j=0;j<=i;j++) {
            if (nails[calc(i,j)] > 0) sum++;
        }
    }
}

int main() {
    int i;
    int m;

    scanf("%d %d", &n, &m);
    for (i=0;i<m;i++) {
        int a, b, x;
        scanf("%d %d %d", &a, &b, &x);
        a--;
        b--;
        x++;
        nails[safe_calc(a,b)]++;
        nails[safe_calc(a,b)+1]--;
        nails[safe_calc(a+x,b)]--;
        nails[safe_calc(a+x,b+x)+1]++;
        nails[safe_calc(a+x+1,b+1)]++;
        nails[safe_calc(a+x+1,b+x+1)]--;
    }

    process();

    printf("%d\n", sum);
}

