		for(int i=1;i<N;i++){
			if(j>=V[i]){break;}
			x[i][j]+=x[i-1][j];
		}
	}
	for(int i=0;i<=n;i++){
		for(int j=0;j<=n-i;j++){
			x[i+j+1][j+1]+=x[i+j][j];
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=i;j++){
			if(x[i][j]>=1){cnt++;}
		}
	}
	cout<<cnt<<endl;
	return 0;
}

