	p1++;
      }
      while(p2 != tilt[i].end() && p2->first == j){
	triangle[i & 1][j][1] += p2->second ? 1 : -1;
	p2++;
      }

      nownum += triangle[i & 1][j][0] + triangle[i & 1][j][1];
      if(nownum > 0)
	ans++;
    }
  }

  cout << ans << endl;

  return 0;
}

