int B[500000];
int X[500000];
int main()
{
	scanf("%d%d",&N,&M);
	for(int i=0;i<M;i++){
		scanf("%d%d%d",&A[i],&B[i],&X[i]);A[i]--;B[i]--;
	}
	int ans=0;
	for(int i=0;i<N;i++){
		int cou=0;
		board.clear();
		for(int j=0;j<M;j++){
			if(A[j]<=i&&i<=A[j]+X[j]){
				board.push_back(P(B[j],B[j]+(i-A[j])+1));
			}
		}
		if(board.size()==0)continue;
		sort(board.begin(),board.end());
		int start=board[0].first;
