		for(int j=1;j<board.size();j++){
			if(start==board[j].first){
				end=max(end,board[j].second);
			}
			else if(end<board[j].first){
				cou+=(end-start);
				start=board[j].first;
				end=board[j].second;
			}
			else{
				end=max(end,board[j].second);
			}
		}
		cou+=(end-start);
		ans+=cou;
	}
	printf("%d\n",ans);
	return 0;
}

