#include<cstdio>
#include<algorithm>
#include<vector>
using namespace std;
#define MAX_N 5005
struct data{
  int l,r;
  bool operator < (const data &p)const{ 
    return ( l == p.l ? r > p.r : l < p.l );
  }
};

int N,M,ans;
vector<data> T[MAX_N];

void calc(vector<data> &a,vector<data> &b){
  vector<data> tmp;
  sort(a.begin(),a.end());

  a.push_back((data){MAX_N,MAX_N-1});
  for(int i=1;i<(int)a.size();i++){
    if(a[i].r<=a[i-1].r)a[i]=a[i-1];
    else tmp.push_back(a[i-1]);
  }

  for(int i=1;i<(int)a.size();i++){
    if(a[i].l<=a[i-1].r+1){
      a[i].l=a[i-1].l;
      a[i].r=max(a[i].r,a[i-1].r);
    }else ans+=(a[i-1].r-a[i-1].l+1);
  }

  for(int i=0;i<(int)tmp.size();i++){
    tmp[i].r--;
    if(tmp[i].r<tmp[i].l)continue;
    b.push_back(tmp[i]);
  }
}

int main(){
  int a,b,c;
  scanf("%d%d",&N,&M);
  for(int i=0;i<M;i++){
    scanf("%d%d%d",&a,&b,&c);
    T[a+c].push_back((data){b,b+c});
  }
  ans=0;
  for(int i=N;i>=1;i--)calc(T[i],T[i-1]);
  printf("%d\n",ans);
  return 0;
}

