int INC[5100][5100] = {};
int DEC[5100][5100] = {};

int main() {

	int N, M;
	cin >> N >> M;

	for(int i = 0; i < M; i++){
		int A, B, X;
		cin >> A >> B >> X;
		A = A - B + 1;
		INC[A][B]++;
		INC[A + X + 1][B]--;
		DEC[A - 1][B + 1]++;
		DEC[A - 1][B + X + 2]--;
	}

	for(int i = 0; i <= N + 1; i++){
		int ret = 0;
		int ret2 = 0;
		for(int j = 0; j <= N + 1; j++){
			ret += DEC[i][j];
			DEC[i][j] = ret;
			ret2 += INC[j][i];
			INC[j][i] = ret2;
		}
	}

	/*
	for(int i = 0; i <= N + 1; i++){
		for(int j = 0; j <= N + 1; j++){
			cout << INC[i][j] << " ";
		}
		cout << endl;
	}

	for(int i = 0; i <= N + 1; i++){
		for(int j = 0; j <= N + 1; j++){
			cout << DEC[i][j] << " ";
		}
		cout << endl;
	}
	*/

	int ans = 0;
	for(int i = 0; i <= N + 1; i++){
		int ret = 0;
		for(int j = 1; j <= i - 1; j++){
			int id1 = i - j;
			int id2 = j;
			ret += INC[id1][id2];
			ret -= DEC[id1][id2];
			if(ret > 0){
				ans++;
			}
		}
	}

	cout << ans << endl;

	return 0;
}

