using namespace std;

int N,M;
int change(int y,int x){
  return y-x+N;
}

pair<int,int> change(int z){
  int y,x;
  if(z>=N){
    x=1;
    y=z+x-N;
  }else{
    y=1;
    x=y+N-z;
  }
  return pair<int,int>(y,x);
}

struct data{
  int l,r;
  bool operator < (const data &p)const{
    return (l==p.l?r<p.r:l<p.l);
  }
};

vector<data> t[5001];
vector<data> u[5001];
vector<data> v[10002];
bool visited[5001][5001];

int cnt;
bool rec(int y,int x){
  if(y==0||x==0)return true;
  if(y==N+1||y<x)return true;
  if(visited[y][x]==1)return false;
  visited[y][x]=1;  cnt++;
  bool flg=false;
  flg|=rec(y+1,x);
  flg|=rec(y-1,x);
  flg|=rec(y,x+1);
  flg|=rec(y,x-1);
  return flg;
}

int main(){
  cin>>N>>M;
  for(int i=0;i<M;i++){
    int a,b,c;
    cin>>a>>b>>c;
    t[a+c].push_back((data){b,b+c});
    u[b].push_back((data){a,a+c});
    v[change(a,b)].push_back((data){a,a+c});
  }

  for(int i=1;i<=N;i++){
    sort(t[i].begin(),t[i].end());
    vector<data> &v=t[i];
    v.push_back((data){N+N,0});
    for(int j=1;j<(int)v.size();j++){
      if(v[j].l<=v[j-1].r)v[j].l=v[j-1].l;
      else {
	for(int k=v[j-1].l;k<=v[j-1].r;k++)
	  visited[i][k]=true;
      }
    }
  }
  for(int i=1;i<=N;i++){
    sort(u[i].begin(),u[i].end());
    vector<data> &v=u[i];
    v.push_back((data){N+N,0});
    for(int j=1;j<(int)v.size();j++){
      if(v[j].l<=v[j-1].r)v[j].l=v[j-1].l;
      else{
	for(int k=v[j-1].l;k<=v[j-1].r;k++)
	  visited[k][i]=true;
      }
    }
  }

  for(int i=1;i<N+N;i++){
    sort(v[i].begin(),v[i].end());
    vector<data> &w=v[i];
    w.push_back((data){N+N,0});
    for(int j=1;j<(int)w.size();j++){
      if(w[j].l<=w[j-1].r)w[j].l=w[j-1].l;
      else{
	pair<int,int> p = change(i);
	int x=p.second;
	for(int k=w[j-1].l;k<=w[j-1].r;k++){
	  visited[k][x+k-w[j-1].l]=true;
	}
      }
    }
  }
  int ans=0;

  for(int i=1;i<=N;i++){
    for(int j=1;j<=i;j++){
      ans+=visited[i][j];
      //cout<<visited[i][j];
    }//cout<<endl;
}

  for(int i=1;i<=N;i++){
    for(int j=1;j<=i;j++){
      if(visited[i][j])continue;
      cnt=0;
      if( !rec(i,j) ){
	ans+=cnt;
      }
    }
  }
  cout<<ans<<endl;
  return 0;
}

