int N, M;
int d[5010][5010];
int h[5010][5010];
int t[5010][5010];

int main() {
  cin >> N >> M;
  for (int i=0; i<M; i++) {
    int a, b, x;
    cin >> a >> b >> x;
    a--;
    b--;
    d[a][b]   += 1;
    d[a][b+1] -= 1;
    d[a+x+1][b]   -= 1;
    d[a+x+2][b+1] += 1;
    d[a+x+1][b+x+2] += 1;
    if (x == 1)
      d[a+x+2][b+x+1+1] -= 1;
    else
      d[a+x+2][b+x+1] -= 1;
  }

  for (int i=0; i<=N; i++) {
    for (int j=0; j<=N; j++) {
      h[i][j] = ((i>0&&j>0)? h[i-1][j-1] : 0) + d[i][j];
    }
  }

  for (int i=0; i<=N; i++) {
    int g = 0;
    for (int j=0; j<=N; j++) {
      g += h[j][i];
      t[j][i] = g;
    }
  }

  int ans = 0;
  for (int i=0; i<N; i++) {
    int g = 0;
    for (int j=0; j<=i; j++) {
      g += t[i][j];
      if (g > 0) ans++;
    }
  }
  cout << ans << "\n";
  return 0;
}

