int main() {
	int n, m; std::cin >> n >> m;
	std::vector<std::vector<int>> nails; nails.reserve(n);
	for (auto i = 0; i < n; ++i) {
		nails.emplace_back(n - i, 0);
	}
	for (auto i = 0; i < m; ++i) {
		int a, b, x; std::cin >> a >> b >> x;
		nails[a - b][b - 1] = std::max(nails[a - b][b - 1], x + 1);
	}
	for (auto i = 1; i < n; ++i) {
		nails[i][0] = std::max(nails[i][0], nails[i - 1][0] - 1);
		nails[0][i] = std::max(nails[0][i], nails[0][i - 1] - 1);
	}
	for (auto i = 1; i < n; ++i) {
		for (auto j = 1; j < n - i; ++j) {
			nails[i][j] = std::max({ nails[i][j], nails[i][j - 1] - 1, nails[i - 1][j] - 1 });
		}
	}
	int count = 0;
	for (const auto& line : nails) count += std::count_if(line.begin(), line.end(), [](const int i) {return i > 0; });
	std::cout << count << std::endl;
}

