int main()
{
	cin.tie( 0 );
	ios::sync_with_stdio( false );

	int n, m;
	cin >> n >> m;

	VVI horizontal( n + 1 ), slanting( n + 1 );

	REP( i, 0, horizontal.size() )
	{
		horizontal[i].resize( i + 2, 0 );
		slanting[i].resize( i + 2, 0 );
	}

	REP( i, 0, m )
	{
		int a, b, x;
		cin >> a >> b >> x;

		a--;
		b--;

		horizontal[ a + x + 1 ][b] --;
		horizontal[ a + x + 1 ][ b + x + 1 ] ++;

		slanting[a][b] ++;
		slanting[ a + x + 1 ][ b + x + 1 ] --;
	}

	REP( i, 0, horizontal.size() )
	{
		REP( j, 1, horizontal[i].size() )
		{
			horizontal[i][j] += horizontal[i][ j - 1 ];
		}
	}

	REP( i, 1, slanting.size() )
	{
		REP( j, 1, slanting[i].size() )
		{
			slanting[i][j] += slanting[ i - 1 ][ j - 1 ];
		}
	}

	VVI &board = horizontal;

	REP( i, 0, board.size() )
	{
		REP( j, 0, board[i].size() )
		{
			board[i][j] += slanting[i][j];
		}
	}

	REP( i, 1, board.size() )
	{
		REP( j, 0, board[ i - 1 ].size() )
		{
			board[i][j] += board[ i - 1 ][j];
		}
	}

	int res = 0; 

	REP( i, 0, board.size() - 1 )
	{
		REP( j, 0, board[i].size() - 1 )
		{
			res += 1 <= board[i][j];
		}
	}

	cout << res << endl;

	return 0;
}

