int N, M;

void put(int a, int b, int x) {
  TRI(a,b) ++;
  TRI(a,b+1) --;
  TRI(a+x+1,b) --;
  TRI(a+x+2,b+1) ++;
  TRI(a+x+1,b+x+2) ++;
  TRI(a+x+2,b+x+2) --;
}

void show() {
  for (int a = 1; a < N+1; a++) {
    REP(b,a+1) {
      cout << TRI(a,b);
    }
    cout << endl;
  }
}

void accumulate() {
  show();
  for (int c = 1; c <= N; c++) for (int n = 1; c + n <= N; n++) {
    TRI(c + n, c) += TRI(c + n - 1, c);
  }

  show();
  for (int c = 1; c <= N; c++) for (int n = 1; c + n <= N; n++) {
    TRI(c + n , n) += TRI(c + n - 1, c);
  }

  show();
  for(int a = 1; a <= N; a++) for (int b = 2; b <= N; b++) {
    TRI(a, b) += TRI(a, b - 1);
}

int count() {
  int sum = 0;
  REP(a, N) {
    REP(b, N) {
      if (TRI(a+1,b+1) > 0) sum++;
    }
  }
  return sum;
}

