inline int max(int a,int b){return (a<b)?b:a;}
int main() {
	int N,M,x,y,size;
	pq nail;

	cin >> N >> M;
	REP(i,M){
		scanf("%d%d%d",&x,&y,&size);
		nail.push(ppr(pr(x,y) , size+1));
	}
	int sum=0;
	REP(i,N+1){
		REP(j,N+1)
			maps[(i+1)&1][j]=0;
		while(!nail.empty() &&nail.top().first.first==i){
			maps[i&1][nail.top().first.second]=nail.top().second;
			nail.pop();
		}
		REP(j,N+1){
			maps[(i+1)&1][j+1]=max(maps[(i+1)&1][j+1],maps[i&1][j]-1);
			maps[(i+1)&1][j]=max(maps[(i+1)&1][j],maps[i&1][j]-1);
			if(maps[i&1][j])
				sum++;
		}
	}
	cout << sum << endl;
	return 0;
}

