typedef pair<int,int> pii;
const int N = 5012;

pair<int,int> st[N][N];//ããå ´æã«å·¦ç«¯ã¨å³ç«¯ã®ç¹ãä½åããããæ°ããã
int bp[N][N];
int epL[N][N];
int epR[N][N];

/*
  åãã¤ã³ãã«ã¤ãã¦ãä¸è§å½¢ã®å·¦ç«¯ã¨å³ç«¯ããããã¹ã¨ãã®æ°ãè¦ãã¦ããã
  ãã¨ã¯å·¦ç«¯ããå¨é¨è¦ã¦ããã¨ãã®è¡ã®ãã¡ä½åãéãè¼ªã´ã ã«è¦ããã¦ãããããããã
 */
int solve(int n){
  rep(i,n+1)rep(j,n+1)st[i][j].left = 0,st[i][j].right = 0;
  int ret = 0;
  REP(i,1,n+1){
    rep(j,i+1){
      int l = 0,r = 0;
      if (j-1 >= 0)r = st[i-1][j-1].right;
      if (j != i)l = st[i-1][j].left;
      st[i][j].left  = l;
      st[i][j].right = r;
    }
    rep(j,i+1){
      st[i][j].left+=bp[i][j];
      st[i][j].right+=bp[i][j];
    }

    /*
    cout <<i <<" -th line" << endl;
    rep(j,i+1){
      cout << "(" <<st[i][j].left <<"," << st[i][j].right << ") ";
    }
    cout << endl;
    */

    int k = 0;
    rep(j,i+1){
      k += st[i][j].left;
      if (k)ret++;
      k -= st[i][j].right;
      assert(k >= 0);
    }
    rep(j,i+1){
      st[i][j].left -=epL[i][j];
      st[i][j].right-=epR[i][j];
    }
  }
  return ret;
}

