int main() {
    int N, M;
    cin >> N >> M;

    vector<vector<int>> nail(N + 3, vector<int>(N + 3, 0));
    for(int i = 0; i < M; ++i) {
        int A, B, X;
        cin >> A >> B >> X;
        ++nail[A][B];
        --nail[A][B + 1];
        --nail[A + X + 1][B];
        ++nail[A + X + 1][B + X + 2];
        ++nail[A + X + 2][B + 1];
        --nail[A + X + 2][B + X + 2];
    }

    // 横
    for(int i = 1; i <= N + 2; ++i) for(int j = 1; j <= N + 2; ++j)
        nail[i][j] += nail[i][j - 1];
    // 縦
    for(int j = 1; j <= N + 2; ++j) for(int i = 1; i <= N + 2; ++i)
        nail[i][j] += nail[i - 1][j];
    // 斜め
    for(int i = 1; i <= N + 2; ++i) for(int t = 1; i + t <= N + 2 ; ++t)
        nail[i + t][1 + t] += nail[i + t - 1][1 + t - 1];

    int answer = 0;
    for(int i = 1; i <= N; ++i) for(int j = 1; j <= N; ++j) if(nail[i][j]) ++answer;
