using namespace std;
int main() {
	int N, M;
	cin >> N >> M;
	vector<vector<pair<int, int> > > G(N);
	for (int i = 0; i < M; ++i) {
		int A, B, X;
		cin >> A >> B >> X;
		G[A - 1].push_back(make_pair(B - 1, X + 1));
	}
	vector<int> dp;
	int ans = 0;
	for (int i = 0; i < N; ++i) {
		vector<int> ndp(i + 1);
		for (int j = 0; j < i; ++j) {
			ndp[j] = max(ndp[j], dp[j] - 1);
			ndp[j + 1] = max(ndp[j + 1], dp[j] - 1);
		}
		for (pair<int, int> j : G[i]) {
			ndp[j.first] = max(ndp[j.first], j.second);
		}
		dp = ndp;
		for (int j = 0; j <= i; ++j) {
			if (dp[j] >= 1) ++ans;
		}
	}
	cout << ans << endl;
	return 0;
}

