using namespace std;

typedef pair<int,int> P;

int main() {
    int N,M;
    cin >> N >> M;

    vector<set<P> > kugi(N+1);

    for(int i=0; i<M; ++i) {
        int A,B,X;
        cin >> A >> B >> X;

        for(int j=0; j<=X; ++j) {
            kugi[A+j].insert(P(B,B+j+1)); // [B,B+j+1) = [B,B+j]
        }
    }

    int ans=0;
    for(int i=1; i<=N; ++i) {
        int l=0, r=0;
        for(set<P>::iterator itr=kugi[i].begin(); itr!=kugi[i].end(); ++itr) {
            if(itr->second<=r) continue;
            if(itr->first>=r) {
                l=itr->first;
                r=itr->second;
                ans+=r-l;
            } else if(itr->second>r) {
                ans+=itr->second-r;
                r=itr->second;
            }
        }
    }

    cout << ans << endl;
    return 0;
}

