int sum(int a){
  return N*(N+1)/2 - (N-a)*(N-a+1)/2;
}
int calc_id(int y,int x){
  return sum(x-1)+ y - x;
}
int main(){
  scanf("%d %d",&N,&M);
  memset(G,-1,sizeof(G));
  for(int i=0;i<M;i++){
    int a,b,x;
    scanf("%d %d %d",&a,&b,&x);
    int id = calc_id(a,b);
    G[id] = max(G[id],x);
  }
  int res = 0;
  for(int y=1;y<=N;y++){
    for(int x=1;x<=y;x++){
      int id = calc_id(y,x);
      if(G[id]==-1) continue;
      res++;
      if(y+1 > N) continue;
      int id1 = calc_id(y+1,x);
      int id2 = calc_id(y+1,x+1);
      G[id1]=max(G[id1],G[id]-1);
      G[id2]=max(G[id2],G[id]-1);
    }
  }
  printf("%d\n",res);
}

