    {
        for(int j = 0; j <= i; j++)
        {
            int mem = fid[i][j];
            if(i == 0);
            else if(j == 0)
            {
                fid[i][j] = max(fid[i - 1][j] - 1,mem);
            }
            else if(i == j)
            {
                fid[i][j] = max(fid[i - 1][j - 1] - 1,mem);
            }
            else
            {
                fid[i][j] = max
                           (max(
                            fid[i - 1][j] - 1,fid[i - 1][j - 1] - 1),mem);
            }
            if(fid[i][j])ans++;
        }
    }
    printf("%d\n",ans);
}

