		public R(int a, int b, int x) {
			this.a = a;
			this.b = b;
			this.x = x;
		}
		public int compareTo(R o) {
			return o.x-x;
		}
	}

	int X = 5001;
	int[][] t;
	void run(){
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt(), m = sc.nextInt();
