static void solve(){
 int n=ir.nextInt();
 AL[] seg=new AL[n];
 for(int i=0;i<n;i++) seg[i]=new AL();
 int m=ir.nextInt();
 for(int i=0;i<m;i++){
  int a=ir.nextInt()-1;
  int b=ir.nextInt()-1;
  int x=ir.nextInt();
  for(int j=a;j<=a+x;j++){
   seg[j].add(new int[]{b,b+j-a});
  }
 }
