import java.util.Scanner;


public class Main {

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		int N = sc.nextInt(), M = sc.nextInt();

		int[][] grid = new int[N+2][N+2];

		// 右下の配置
		for (int i = 0 ; i < M ; i++) {
			int a = sc.nextInt(), b= sc.nextInt(), x = sc.nextInt();
			if (grid[a+x][b] < x+1) grid[a+x][b] = x+1;
		}

		// 上方向への展開
		for (int x = N ; x > 0 ; x--) {
			for (int y = 1 ; y <= N ; y++) {
				grid[x][y] = Math.max(grid[x][y], grid[x][y-1]-1);
			}
		}

		// 左方向への展開
		long count = 0;
		for (int y = 1 ; y <= N ; y++) {
			for (int x = N-1 ; x >= 0 ; x--) {
				if (grid[x+1][y] != 0) { // 右隣がゼロではない
					grid[x][y] = Math.max(grid[x][y], grid[x+1][y]-1);
					count++;
				}
			}
		}

		/*for (int y = N ; y > 0 ; y--) {
			for (int x = 1 ; x <= N ; x++) {
				System.out.print(grid[x][y]);
			}
			System.out.print("\n");
		}
		System.out.print("\n");*/

		System.out.println(count);

	}
}

import java.util.Scanner;


public class Main {

	void run() {
		Scanner sc = new Scanner(System.in);

		int N = sc.nextInt(), M = sc.nextInt();

		int[][] g = new int[N+1][];
		for (int i = 0 ; i <= N ; i++) {
			g[i] = new int[i+2];
		}

		while (M-- != 0) {
			int a = sc.nextInt(),
				b = sc.nextInt(),
				x = sc.nextInt();
			g[a][b] = Math.max(g[a][b], x+1);
		}

		int count = 0;
		for (int x = 1 ; x <= N ; x++) {
			for (int y = 1 ; y <= x ; y++) {
				g[x][y] = Math.max(g[x][y], Math.max(g[x-1][y]-1, g[x-1][y-1]-1));
				if (g[x][y] > 0) count++;
			}
		}

		System.out.println(count);
	}

	public static void main(String[] args) throws java.lang.Exception {
		new Main().run();
	}

}

import java.util.Scanner;


public class Main {

	public static void main(String[] args) throws java.lang.Exception {
		Scanner sc = new Scanner(System.in);

		int N = sc.nextInt(), M = sc.nextInt();

		int[][] g = new int[N+1][];
		for (int i = 0 ; i <= N ; i++) {
			g[i] = new int[N+2-i];
		}

		for (int m = 0 ; m < M ; m++) {
			int a = sc.nextInt(),
				b = sc.nextInt(),
				x = sc.nextInt();
			g[a-b+1][b] = Math.max(g[a-b+1][b], x+1);
		}

		for (int x = 1 ; x <= N ; x++) {
			for (int y = 1 ; y <= N+1-x ; y++) {
				g[x][y] = Math.max(g[x][y], Math.max(g[x-1][y]-1, g[x][y-1]-1));
			}
		}

		long count = 0;
		for (int x = 0 ; x <= N ; x++) {
			for (int y = 0 ; y <= N+1-x ; y++) {
				if (g[x][y] > 0) count++;
				//System.out.print(g[x][y]);
			}
			//System.out.print("\n");
		}
		//System.out.print("\n");

		System.out.println(count);

	}

}

