  cin >> n >> m;
  for(int i=0;i<n;i++){
    cin >> p[i];
  }
  for(int i=0;i<m;i++){
    cin >> c[i] >> e[i];
  }
  limit[0]=c[0];//ここから４行、まんじゅうの箱の類積和
  hakone[0]=e[0];

  for(int i=1;i<=m;i++){
    limit[i]=limit[i-1]+c[i];
