  }

  sort(p,p+n,greater<int>());//まんじゅうのソート

  int man[10001];
  man[0]=p[0];
  for(int i=1;i<n;i++){//まんじゅうの類積和をもとめる
    man[i]=man[i-1]+p[i];
  }

  fill(dp[0],dp[10001],INF);

  for(int i=0;i<=m;i++){//自明の解　まんじゅうを入れないときは絶対に０
    dp[i][0]=0;
  }

  for(int i=1;i<=m;i++){
    for(int j=limit[i-1];j>0;j--){
      if(j<=c[i-1]){
	dp[i][j]=min(e[i-1],dp[i-1][j]);
