const long long max = 1000000000;

long long p[10001];
long long c[501];
long long e[501];
long long dp[501][10001];

int main(){

	int m, n;
	std::cin >> m >> n;

	for (int i = 0; i < m; i++){
		std::cin >> p[i];
	}
	std::sort(p, p + m, std::greater<long long>());

	for (int i = 0; i < m; i++){
		p[i] += p[i - 1];
	}

	for (int i = 0; i < n; i++){
		std::cin >> c[i] >> e[i];
	}

	for (int i = 0; i <= 500; i++){
		for (int j = 0; j <= 10000; j++){
			dp[i][j] = max;
		}
	}
	dp[1][0] = 0;
	dp[1][c[0]] = e[0];

	for (int i = 1; i < n; i++){
		for (int j = 0; j <= 10000; j++){
			if (dp[i][j] != max){
				dp[i + 1][j] = std::min(dp[i + 1][j], dp[i][j]);
				if (j + c[i] <= 10000)dp[i + 1][j + c[i]] = std::min(dp[i + 1][j + c[i]], dp[i][j] + e[i]);
			}
		}
	}

	for (int i = 9999; i >= 0; i--){
		dp[n][i] = std::min(dp[n][i], dp[n][i + 1]);
	}

	long long max = 0;
	for (int i = 1; i <= m; i++){
		max = std::max(max, p[i - 1] - dp[n][i]);
	}

	std::cout << max << std::endl;

	return 0;
}

