i64 dp[502][10004]={0}, cumsum[10004]={0};

int main(){
    int m, n;
    cin >> m >> n;
    vector<i64> p;
    for(int i=0;i<m;++i){
        i64 tmp;
        cin >> tmp;
        p.push_back(tmp);
    }
    vector<Box> b;
    for(int i=0;i<n;++i){
        i64 c, e;
        cin >> c >> e;
        b.push_back((Box){c, e});
    }

    sort(p.begin(), p.end(), greater<i64>());
    for(int i=1;i<=m;++i)cumsum[i] = cumsum[i-1] + p[i-1];
    for(i64 i=0;i<n;++i){
        for(i64 j=0;j<=m;++j){
            dp[i+1][j] = max(dp[i+1][j], dp[i][j]);
            i64 cnt = min(b[i].c, m-j);
            i64 price = cumsum[j+cnt] - cumsum[j];
            i64 benefit = price - b[i].e;
            dp[i+1][j+cnt] = max(dp[i+1][j+cnt], dp[i][j] + benefit);
        }
    }

    i64 ans = 0;
    for(int i=0;i<=m;++i)ans = max(ans, dp[n][i]);

    cout << ans << endl;

    return 0;
