int dp[510][10010];
int main()
{
    int M,N,P[10010],C[510],E[510],ans = 0;
    scanf("%d%d",&M,&N);
    for(int i = 0; i < M; i++)scanf("%d",&P[i]);
    sort(P,P + M);
    for(int i = M - 2; i >= 0; i--)P[i] = P[i] + P[i + 1];
    //for(int i = M - 1; i >= 0; i--)printf("%4d",P[i]);printf("\n");
    for(int i = 0; i < N; i++)scanf("%d%d",&C[i],&E[i]);
    for(int i = 0; i < N + 1; i++)
    {
        for(int j = 0; j < M + 1; j++)
        {
            if(j == 0)dp[i][j] = 0;
            else dp[i][j] = 10000000;
            if(i != 0)
            {
                dp[i][j] = dp[i - 1][j];
                if(j >= C[i - 1])dp[i][j] = min(dp[i][j],dp[i - 1][j - C[i - 1]] + E[i - 1]);
            }
        }
        if(i != 0 && C[i - 1] > M)dp[i][M] = min(dp[i][M],E[i - 1]);
        for(int j = M - 1; j >= 0; j--)dp[i][j] = min(dp[i][j],dp[i][j + 1]);
    }
