    {
        for(int j = 0; j < M + 1; j++)
        {
            printf("%9d",dp[i][j]);
        }
        printf("\n");
    }*/
    for(int i = 0; i < M; i++)ans = max(ans,P[M - 1 - i] - dp[N][i + 1]);
    printf("%d\n",ans);
    return 0;
}

