int dp[10001][501]={};

int calc(int a,int b){
	int ans=INF;
	if(b<0)return ans;
	if(a<=0)return 0;
	if(dp[a][b]>-1)return dp[a][b];
	ans=calc(a,b-1);
	if(a<=C[b]){
		ans=min(ans,E[b]);
	}else{
		ans=min(ans,calc(a-C[b],b-1)+E[b]);
	}

	dp[a][b]=ans;
	return ans;
}
