    //cout<<n<<" "<<kin<<endl;
    if(n==N||kin>M){
        //cout<<n<<" "<<kin<<endl;
        return 0;
    }
    FOR(i,n,n+C[kin].second){
        if(i<=N){
            if(i==n){
                res=max(cal(i,kin+1),res);
            }
            else if(V[i]-V[n]>C[kin].first)res=max(cal(i,kin+1)+V[i]-V[n]-C[kin].first,res);
        }
    }
    return mem[n][kin]=res;
}


