ll dp[10005][512];

ll dfs(int m, int n){
  if(m>M || n>=N) return 0;
  ll &res = dp[m][n];
  if(res >= 0) return res;
  res = 0;
  if(m<=M) res = max(res, dfs(min(M, m+C[n]), n+1) + P[min(M,m+C[n])]-P[m] - E[n]);
  res = max(res, dfs(m, n+1));
  return res;
}

