#include <iostream>
#include <queue>
using namespace std;

int main() {

	int dp[10010];
	int sum[10010] = {0};
	int inf = 1222333444;

	int m, n;
	int i, j;
	priority_queue <int> p;
	int c[500], e[500];

	cin >> m >> n;

	for(i = 0; i < m; i++) {
		int temp;
		cin >> temp;
		p.push(temp);
	}

	for(i = 0; i < n; i++) {
		cin >> c[i] >> e[i];
	}

	for(i = 0; i < 10010; i++) {
		dp[i] = inf;
	}

	dp[0] = 0;

	int cap = 0;
	for(i = 1; i <= n; i++) {
		cap += c[i-1];
		for(j = cap; j >= 0; j--) {
			dp[j] = min(dp[j], dp[max(j-c[i-1], 0)] + e[i-1]);
		}
	}

	int ans = 0;

	for(i = 1; i <= cap; i++) {
		sum[i] = sum[i-1] + p.top();
		p.pop();
		ans = max(ans, sum[i] - dp[i]);
	}

	cout << ans << endl;

	return 0;
}

#include <iostream>
