using namespace std;
int N, M, P[10009], C[509], E[509], dp[509][10009];
int main() {
	cin >> M >> N;
	for (int i = 0; i < M; i++) cin >> P[i + 1];
	for (int i = 0; i < N; i++) cin >> C[i] >> E[i];
	fill(dp[0] + 1, dp[0] + M + 1, 999999999);
	for (int i = 0; i < N; i++) {
		for (int j = 0; j <= M; j++) {
			dp[i + 1][j] = dp[i][j];
			if (j >= C[i]) dp[i + 1][j] = min(dp[i + 1][j], dp[i][j - C[i]] + E[i]);
		}
		for (int j = M - 1; j >= 0; j--) dp[i + 1][j] = min(dp[i + 1][j], dp[i + 1][j + 1]);
	}
	sort(P + 1, P + M + 1, greater<int>());
	for (int i = 1; i <= M; i++) P[i] += P[i - 1];
	int ret = 0;
	for (int i = 0; i <= M; i++) ret = max(ret, P[i] - dp[N][i]);
	cout << ret << endl;
	return 0;
}

