vector<int> box_value(500);

int sum_manju[10000];

int main()
{
	cin >> M >> N;

	FOR(i,0,M-1)
	{
		cin >> manju[i];
	}

	FOR(i,0,N - 1)
	{
		cin >> box_size[i] >> box_value[i];
	}

	sort(manju,manju + M);

	reverse(manju,manju + M);

	sum_manju[0] = manju[0];

	FOR(i,1,M - 1)
	{
		sum_manju[i] += sum_manju[i - 1] + manju[i];
	}

	int result = 0;
	int left_index = 0;
	while(left_index != M)
	{
		int now_result = 0;
		int result_r = M;
		int box_index = 0;
		FOR(bi,0,N - 1)
		{
			int right_index = min(M - 1,left_index + box_size[bi] - 1);

			int value = sum_manju[right_index] - sum_manju[left_index] + manju[left_index] - box_value[bi];

			if(now_result < value)
			{
				if(right_index < result_r)
				{
					now_result = value;
					result_r = right_index;
					box_index = bi;
				}
			}
		}
		if(now_result == 0)
		{
			break;
		}
		result += now_result;
		box_size.erase(box_size.begin() + box_index);
		box_value.erase(box_value.begin() + box_index);

		left_index = result_r + 1;

	}

	cout << result << endl;
	return 0;
}

