    int ans = 0;
    int prof = csarr.query(cnt, min(m - 1, cnt + c[i] - 1)) - e[i];

    chmax(ans,  prof + dfs(i + 1, cnt + c[i]));//use box
    chmax(ans, dfs(i + 1, cnt));

    return dp[i][cnt] = ans;


}


