int m, n, p[10005], c[555], e[555];
int memo[10005][555];

int dp(int i, int j,int m,int n)
{
	if (i == n || j >= m) return 0;
	if (memo[i][j] != -1) return memo[i][j];
	int res;
	int sum = 0;
	for (int k = j; k < j + c[i] && k< m; k++) sum += p[k];
	if (sum <= e[i]) res = dp(i + 1, j,m,n);
	else
	{
		res = max(dp(i + 1, j,m,n), dp(i + 1, j + c[i],m,n) + sum - e[i]);
	}
	return memo[i][j] = res;
}

int main()
{
	int n, m;
	ms(memo, -1);
	cin >> m >> n;

	REP(i, m) cin >> p[i];
	REP(i, n) cin >> c[i] >> e[i];
	sort(p, p + m, greater<int>());

	cout << dp(0, 0,m,n) << endl;
	return 0;
}

