    for(int i=1;i<=m;i++){
        cin>>sz[i]>>cost[i];
    }
    sort(a+1,a+1+n,greater<int>());
    for(int i=1;i<=n;i++) sum[i]=sum[i-1]+a[i];
    for(int i=1;i<=m;i++) siz[i]=siz[i-1]+sz[i];
    for(int i=1;i<=m;i++){
        for(int j=1;j<=min(siz[i],n);j++){
            int low=max(0,j-sz[i]);
            dp[i][j]=max(dp[i-1][j],dp[i-1][low]+sum[j]-sum[low]-cost[i]);
        }
    }
    for(int i=0;i<=n;i++) ans=max(ans,dp[m][i]);
    cout<<ans<<endl;
}

