	for (int k = j; k < j + c[i] && k < m; k++) sum += p[k];
	if (sum <= e[i]) res = dp(i + 1, j);
	else
	{
		res = max(dp(i + 1, j), dp(i + 1, j + c[i]) + sum - e[i]);
	}
	return memo[i][j] = res;
}

int main()
{
	ms(memo, -1);
	cin >> m >> n;
	REP(i, m) cin >> p[i];
	REP(i, n) cin >> c[i] >> e[i];
	sort(p, p + m, greater<int>());
	cout << dp(0, 0) << endl;
	return 0;
}

