int N,M;
int man[10001];
int big[501];
int price[500];
int dp[501][10001];
int cost[501];
int ans;

int main() {
	ios::sync_with_stdio(false);
	cin >> N >> M;
	for (int i = 0; i < N; i++)cin >> man[i];
	for (int i = 1; i <= M; i++)cin >> big[i] >> price[i];
	stable_sort(man, man + N);
	reverse(man, man + N);
	for (int i = 1; i < N; i++) man[i] = man[i - 1] + man[i];
	for (int i = N; i >= 1; i--)man[i] = man[i - 1];
	man[0] = 0;
	for (int i = 0; i <= M; i++) {
		for (int j = 0; j <= N; j++) {
			dp[i][j] = MOD;
		}
	}
	dp[0][0] = 0;
	for (int i = 1; i <= M; i++) {
		for (int j = 0; j <= N; j++)dp[i][j] = dp[i - 1][j];
		for (int j = 0; j <= N; j++) {
			dp[i][min(j + big[i], N)] = min(dp[i][min(j+big[i], N)], dp[i - 1][j] + price[i]);
		//	cout << i << " " << j << " " << dp[i][j] << endl;
		}
	}
	for (int i = N; i >= 0; i--) {
		if (!dp[M][i])cost[i] = dp[M][i];
		else if (!cost[i + 1])cost[i] = dp[M][i];
		else cost[i] = min(cost[i + 1], dp[M][i]);
	}
	for (int i = 0; i <= N; i++) {
	//	cout << i << " " << man[i] << " " << cost[i] << endl;
		if (!cost[i])continue;
		ans = max(man[i] - cost[i], ans);
	}
	cout << ans << endl;
	return 0;
}

