typedef pair<int, PII> PPII;
typedef pair<int, PPII> PPPII;

class Union_find{
private:
	vector<int> par;
	vector<int> rank;
	int n;

public:
	Union_find(int a){
		n = a;
		for(int i = 0;i < n;i++){
			par.push_back(i);
			rank.push_back(0);
		}
	}

	int find(int x){
		if(par[x] == x){
			return x;
		}else{
			return par[x] = find(par[x]);
		}
	}

	void unite(int x, int y){
		x = find(x);
		y = find(y);
		if(x == y) return;

		if(rank[x] < rank[y]){
			par[x] = y;
		}else{
			par[y] = x;
			if(rank[x] == rank[y]) rank[x]++;
		}
	}

	bool same(int x, int y){
		return find(x) == find(y);
	}
};

int main(void){
	int M, N;
	ll res = 0;
	int mj[10010];
	PII box[505];
	cin >> M >> N;
	REP(i, M){
		cin >> mj[i];
	}
	sort(mj, mj+M, greater<int>());
	REP(i, N){
		int c, e;
		cin >> c >> e;
		box[i] = {c, e};
	}
	sort(box, box+M, greater<PII>());
	bool used_b[505] = {};
	int used_m = 0;
	while(1){
		int add = 0;
		double max_rieki = 0;
		int b = -1;
		REP(i, N){
			if(used_b[i]) continue;
			int sum = 0;
			REP(j, box[i].first){
				if(used_m+j < M)
					sum += mj[used_m+j];
			}
			if(max_rieki < (double)(sum-box[i].second)/(double)box[i].first){
				add = box[i].first;
				max_rieki = (double)(sum-box[i].second)/(double)box[i].first;
				b = i;
			}
		}
		res += max_rieki*add;
		if(add == 0)
			break;
		if(used_m >= M)
			break;
		used_m += add;
		used_b[b] = true;
	}
	cout << res << endl;
}

