LL cal(int n,int kin){
    LL res=0;
    if(mem[n][kin]!=-1){
        //cout<<"hoge"<<endl;
        return mem[n][kin];
    }
    //cout<<n<<" "<<kin<<endl;
    if(n==N||kin>M){
        return 0;
    }
    FOR(i,n,n+C[kin].second){
        if(i<=N){
            if(i==n){
                //cout<<i<<" "<<kin<<" "<<V[i]<<endl;
                res=max(cal(i,kin+1),res);
            }
            else if(i==n+C[kin].second&&V[i]-V[n]>=C[kin].first)res=max(cal(i,kin+1)+V[i]-V[n]-C[kin].first,res);
            else res=max(V[i]-V[n]-C[kin].first,res);
        }
    }
    return mem[n][kin]=res;
}


