using namespace std;

#define INF 10000000

typedef pair<int,int> P;

int main()
{
    int M, N;
    cin >> M >> N;

    vector<int> man;
    vector<P> pack;

    for(int i=0; i<M; ++i) {
        int temp;
        cin >> temp;
        man.push_back(temp);
    }

    sort(man.rbegin(),man.rend());

    for(int i=1; i<M; ++i) {
        man[i]+=man[i-1];
    }

    for(int i=0; i<N; ++i) {
        int c,e;
        cin >> c >> e;
        pack.push_back(make_pair(c,e));
    }

    int dp[N][M];

    for(int j=0; j<M; ++j) {
        dp[0][j]=INF;
        dp[1][j]=INF;
    }

    for(int i=0; i<M; ++i) {
        if(i<pack[0].first) dp[0][i]=pack[0].second;
    }

    for(int i=1; i<N; ++i) {
        for(int j=0; j<M; ++j) {
            if(j<pack[i].first) {
                dp[i%2][j]=min(dp[i%2][j],dp[(i-1)%2][j]);
                dp[i%2][j]=min(dp[i%2][j],pack[i].second);
            }
            if(j+pack[i].first<M) {
                dp[i%2][j+pack[i].first]=min(dp[(i-1)%2][j+pack[i].first],dp[(i-1)%2][j]+pack[i].second);
            }
        }
    }

    long long ans=0;
    for(int i=0; i<M; ++i) {
        ans=max(ans,man[i]-dp[(N-1)%2][i]);
    }

    cout << ans << endl;



    return 0;
}

