void Solve(){
	int m,n;
	cin >> m >> n;
	vector<int> p(m),c(n),e(n);
	rep(i,0,m) cin >> p[i];
	rep(i,0,n) cin >> c[i] >> e[i];

	sort(p.rbegin(),p.rend());
	vector<int> sum(m+1);
	rep(i,0,m) sum[i+1]=sum[i]+p[i];

	int ans=max(0,sum[min(c[0],m)]-e[0]);
