		for(int i=m;i>=temp;i--)val[i]=min(val[i],val[i-temp]+cost);
		for(int i=0;i<temp;i++)val[i]=min(val[i],cost);
	}
	for(int i=0;i<=m;i++)ans=max(ans,v[i]-val[i]);
	printf("%d\n",ans);
}

